// src/pages/Admin.jsx
import { useState, useRef, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Header from '../components/Header';

export default function Admin() {
  const navigate = useNavigate();
  const [title, setTitle] = useState('');
  const [desc, setDesc] = useState('');
  const [questions, setQuestions] = useState([
    { text: '', image: null, options: ['', '', ''], correct: 0, optionCount: 2 }
  ]);
  const [current, setCurrent] = useState(0);
  const containerRef = useRef(null);

  // Прокрутка к текущему вопросу
  useEffect(() => {
    if (containerRef.current) {
      const scrollContainer = containerRef.current;
      const scrollAmount = scrollContainer.clientWidth * current;
      scrollContainer.scrollTo({
        left: scrollAmount,
        behavior: 'smooth'
      });
    }
  }, [current]);

  const addQuestion = () => {
    setQuestions(prev => [
      ...prev,
      { text: '', image: null, options: ['', '', ''], correct: 0, optionCount: 2 }
    ]);
  };

  const updateQuestion = (field, value) => {
    setQuestions(prev =>
      prev.map((q, i) => (i === current ? { ...q, [field]: value } : q))
    );
  };

  const updateOption = (index, value) => {
    setQuestions(prev =>
      prev.map((q, i) => {
        if (i !== current) return q;
        const options = [...q.options];
        options[index] = value;
        return { ...q, options };
      })
    );
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file && file.type.startsWith('image/')) {
      const reader = new FileReader();
      reader.onload = () => updateQuestion('image', reader.result);
      reader.readAsDataURL(file);
    }
  };

  const removeImage = () => {
    updateQuestion('image', null);
  };

  const goToNext = () => {
    if (current < questions.length - 1) {
      setCurrent(current + 1);
    } else {
      const quiz = { title, desc, questions };
      console.log('Сохранённый квиз:', quiz);
      alert('Викторина сохранена!');
      navigate('/main');
    }
  };

  const goToPrev = () => {
    if (current > 0) {
      setCurrent(current - 1);
    } else {
      if (window.confirm('Выйти без сохранения? Все изменения будут потеряны.')) {
        navigate('/main');
      }
    }
  };

  return (
    <>
      <Header />
      <div className="container" style={{ marginTop: '20px' }}>
        {/* Основной контейнер */}
        <div style={{
          background: 'white',
          borderRadius: '20px',
          boxShadow: '0 15px 40px rgba(0,0,0,0.15)',
          overflow: 'hidden',
          maxWidth: '100%',
          margin: '0 auto',
          display: 'flex',
          flexDirection: 'column'
        }}>
          {/* Заголовок и базовая информация */}
          <div style={{
            padding: '30px 40px',
            borderBottom: '1px solid #e2e8f0',
            background: '#f8f9fa'
          }}>
            <h2 style={{ color: '#2d3748', marginBottom: '15px' }}>Создание викторины</h2>
            <div style={{ marginBottom: '15px' }}>
              <label>Название викторины</label>
              <input
                type="text"
                className="input"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                placeholder="Например: Космос и звёзды"
                style={{ width: '100%', marginTop: '8px' }}
              />
            </div>
            <div>
              <label>Описание</label>
              <input
                type="text"
                className="input"
                value={desc}
                onChange={(e) => setDesc(e.target.value)}
                placeholder="Краткое описание викторины"
                style={{ width: '100%', marginTop: '8px' }}
              />
            </div>
          </div>

          {/* Карусель вопросов */}
          <div
            ref={containerRef}
            style={{
              display: 'flex',
              overflowX: 'hidden',
              scrollBehavior: 'smooth',
              flex: 1,
              position: 'relative'
            }}
          >
            {questions.map((q, idx) => (
              <div
                key={idx}
                style={{
                  minWidth: '100%',
                  padding: '40px',
                  display: idx === current ? 'block' : 'none'
                }}
              >
                {/* Форма вопроса — ШИРОКАЯ */}
                <div style={{
                  background: 'white',
                  padding: '40px',
                  borderRadius: '15px',
                  border: '1px solid #e2e8f0',
                  boxShadow: '0 4px 12px rgba(0,0,0,0.08)',
                  maxWidth: '100%',
                  margin: '0 auto'
                }}>
                  <h3 style={{ color: '#2d3748', marginBottom: '25px', fontSize: '22px' }}>
                    Вопрос {idx + 1} из {questions.length}
                  </h3>

                  {/* Текст вопроса */}
                  <div style={{ marginBottom: '25px' }}>
                    <label>Текст вопроса</label>
                    <input
                      type="text"
                      className="input"
                      value={q.text}
                      onChange={(e) => updateQuestion('text', e.target.value)}
                      placeholder="Введите вопрос"
                      style={{ width: '100%', marginTop: '8px', fontSize: '16px', padding: '16px' }}
                    />
                  </div>

                  {/* Количество вариантов */}
                  <div style={{ marginBottom: '25px' }}>
                    <label>Количество вариантов ответов</label>
                    <select
                      value={q.optionCount}
                      onChange={(e) => {
                        const count = Number(e.target.value);
                        const newOptions = Array(count).fill('');
                        if (q.options) {
                          for (let i = 0; i < Math.min(count, q.options.length); i++) {
                            newOptions[i] = q.options[i];
                          }
                        }
                        setQuestions(prev =>
                          prev.map((item, i) =>
                            i === current
                              ? { ...item, optionCount: count, options: newOptions, correct: 0 }
                              : item
                          )
                        );
                      }}
                      style={{
                        width: '100%',
                        padding: '16px',
                        border: '1px solid #e2e8f0',
                        borderRadius: '10px',
                        fontSize: '16px',
                        marginTop: '8px'
                      }}
                    >
                      <option value={2}>2 варианта</option>
                      <option value={3}>3 варианта</option>
                      <option value={4}>4 варианта</option>
                    </select>
                  </div>

                  {/* Изображение */}
                  <div style={{ marginBottom: '25px' }}>
                    <label>Изображение к вопросу (опционально)</label>
                    <div style={{
                      border: '2px dashed #e2e8f0',
                      borderRadius: '10px',
                      padding: '25px',
                      backgroundColor: '#f7fafc',
                      marginTop: '8px',
                      cursor: 'pointer',
                      textAlign: 'center'
                    }}>
                      {q.image ? (
                        <div style={{
                          display: 'flex',
                          flexDirection: 'column',
                          alignItems: 'center',    // ← Центрирует по горизонтали
                          justifyContent: 'center',
                          gap: '12px'              // ← Красивый отступ между изображением и кнопкой
                        }}>
                          <img
                            src={q.image}
                            alt="Превью"
                            style={{
                              maxWidth: '100%',
                              width: 'auto', 
                              height: 'auto',
                              maxHeight: '200px',
                              borderRadius: '10px'
                            }}
                          />
                          <button
                            onClick={removeImage}
                            style={{
                              background: '#e53e3e',
                              color: 'white',
                              border: 'none',
                              padding: '6px 12px',
                              borderRadius: '6px',
                              fontSize: '14px',
                              cursor: 'pointer'
                            }}
                          >
                            Удалить изображение
                          </button>
                        </div>
                      ) : (
                        <label style={{ cursor: 'pointer' }}>
                          📎 Нажмите, чтобы добавить изображение
                          <input
                            type="file"
                            accept="image/*"
                            onChange={handleImageUpload}
                            style={{ display: 'none' }}
                          />
                        </label>
                      )}
                    </div>
                  </div>

                  {/* Варианты ответов */}
                  <div style={{ marginBottom: '25px' }}>
                    <label>Варианты ответов</label>
                    {Array.from({ length: q.optionCount }, (_, i) => (
                      <div key={i} style={{
                        display: 'flex',
                        alignItems: 'center',
                        gap: '12px',
                        marginBottom: '12px'
                      }}>
                        <input
                          type="radio"
                          checked={q.correct === i}
                          onChange={() => updateQuestion('correct', i)}
                        />
                        <input
                          type="text"
                          className="input"
                          value={q.options[i] || ''}
                          onChange={(e) => updateOption(i, e.target.value)}
                          placeholder={`Вариант ${i + 1}`}
                          style={{ flex: 1, padding: '12px', fontSize: '16px' }}
                        />
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            ))}
          </div>

          {/* Навигация */}
          <div style={{
            padding: '20px 40px',
            display: 'flex',
            gap: '12px',
            borderTop: '1px solid #e2e8f0',
            background: 'white'
          }}>
            <button onClick={goToPrev} className="btn" style={{ flex: 1, padding: '14px' }}>
              {current === 0 ? 'Отмена' : 'Назад'}
            </button>
            <button onClick={addQuestion} className="btn" style={{ flex: 1, padding: '14px' }}>
              + Добавить вопрос
            </button>
            <button onClick={goToNext} className="btn-primary" style={{ flex: 1, padding: '14px' }}>
              {current === questions.length - 1 ? 'Сохранить' : 'Далее'}
            </button>
          </div>
        </div>
      </div>
    </>
  );
}
