// src/pages/Admin.jsx
import { useState, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Header from '../components/Header';

export default function Admin() {
  const navigate = useNavigate();
  const [title, setTitle] = useState('');
  const [desc, setDesc] = useState('');
  const [questions, setQuestions] = useState([
    { text: '', image: null, options: ['', '', ''], correct: 0, optionCount: 2 }
  ]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const carouselRef = useRef(null);

  const addQuestion = () => {
    setQuestions([
      ...questions,
      { text: '', image: null, options: ['', '', ''], correct: 0, optionCount: 2 }
    ]);
    setCurrentQuestionIndex(questions.length);
  };

  const updateQuestion = (field, value) => {
    const newQuestions = [...questions];
    newQuestions[currentQuestionIndex][field] = value;

    if (field === 'optionCount') {
      const count = parseInt(value, 10);
      newQuestions[currentQuestionIndex].options = Array(count).fill('');
      if (newQuestions[currentQuestionIndex].correct >= count) {
        newQuestions[currentQuestionIndex].correct = 0;
      }
    }

    setQuestions(newQuestions);
  };

  const updateOption = (oIndex, value) => {
    const newQuestions = [...questions];
    newQuestions[currentQuestionIndex].options[oIndex] = value;
    setQuestions(newQuestions);
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        updateQuestion('image', reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const removeImage = () => {
    updateQuestion('image', null);
  };

  const goToNext = () => {
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      // Это последний вопрос — сохраняем
      const quiz = { title, desc, questions };
      console.log('Сохранённая викторина:', quiz);
      alert('Викторина сохранена!');
      navigate('/main');
    }
  };

  const goToPrev = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(currentQuestionIndex - 1);
    } else {
      // На первом вопросе — выход
      if (window.confirm('Вы уверены, что хотите выйти? Все изменения будут сохранены.')) {
        navigate('/main');
      }
    }
  };

  const current = questions[currentQuestionIndex];

  return (
    <>
      <Header />
      <div className="container">
        <h1 style={{ fontSize: '28px', color: '#2d3748', marginBottom: '20px' }}>Создание викторины</h1>

        {/* Основная информация */}
        {currentQuestionIndex === -1 && (
          <div style={{ marginBottom: '30px' }}>
            <div style={{ marginBottom: '20px' }}>
              <label>Название викторины</label>
              <input
                type="text"
                className="input"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                placeholder="Например: Космос и звёзды"
                style={{ marginTop: '8px', width: '100%', padding: '15px' }}
              />
            </div>
            <div style={{ marginBottom: '20px' }}>
              <label>Описание</label>
              <input
                type="text"
                className="input"
                value={desc}
                onChange={(e) => setDesc(e.target.value)}
                placeholder="Краткое описание викторины"
                style={{ marginTop: '8px', width: '100%', padding: '15px' }}
              />
            </div>
            <button onClick={() => setCurrentQuestionIndex(0)} className="btn-primary">
              Начать добавление вопросов
            </button>
          </div>
        )}

        {/* Карусель вопросов */}
        <div
          ref={carouselRef}
          style={{
            display: 'flex',
            overflowX: 'hidden',
            scrollBehavior: 'smooth',
            gap: '20px',
            padding: '10px 0'
          }}
        >
          {questions.map((q, qIndex) => (
            <div
              key={qIndex}
              style={{
                minWidth: '100%',
                maxWidth: '100%',
                flex: '0 0 100%',
                opacity: qIndex === currentQuestionIndex ? 1 : 0,
                position: qIndex === currentQuestionIndex ? 'relative' : 'absolute',
                left: 0,
                transition: 'opacity 0.3s ease'
              }}
            >
              <div
                style={{
                  background: '#f8f9fa',
                  padding: '30px',
                  borderRadius: '15px',
                  border: '1px solid #e2e8f0',
                  marginBottom: '20px'
                }}
              >
                <h3 style={{ color: '#2d3748', marginBottom: '15px' }}>
                  Вопрос {qIndex + 1} из {questions.length}
                </h3>

                <div style={{ marginBottom: '15px' }}>
                  <label>Текст вопроса</label>
                  <input
                    type="text"
                    className="input"
                    value={q.text}
                    onChange={(e) => updateQuestion('text', e.target.value)}
                    placeholder="Введите вопрос"
                    style={{ marginTop: '8px', width: '100%', padding: '15px' }}
                  />
                </div>

                <div style={{ marginBottom: '15px' }}>
                  <label>Количество вариантов</label>
                  <select
                    value={q.optionCount}
                    onChange={(e) => updateQuestion('optionCount', e.target.value)}
                    style={{
                      width: '100%',
                      padding: '15px',
                      border: '1px solid #e2e8f0',
                      borderRadius: '10px',
                      marginTop: '8px'
                    }}
                  >
                    <option value={2}>2 варианта</option>
                    <option value={3}>3 варианта</option>
                    <option value={4}>4 варианта</option>
                  </select>
                </div>

                <div style={{ marginBottom: '15px' }}>
                  <label>Изображение (опционально)</label>
                  <div
                    style={{
                      border: '2px dashed #e2e8f0',
                      borderRadius: '10px',
                      padding: '15px',
                      textAlign: 'center',
                      backgroundColor: '#f7fafc',
                      cursor: 'pointer',
                      marginTop: '8px'
                    }}
                  >
                    {q.image ? (
                      <div>
                        <img
                          src={q.image}
                          alt="Превью"
                          style={{
                            maxHeight: '150px',
                            maxWidth: '100%',
                            borderRadius: '8px',
                            marginBottom: '10px'
                          }}
                        />
                        <button
                          onClick={removeImage}
                          style={{
                            background: '#e53e3e',
                            color: 'white',
                            border: 'none',
                            padding: '5px 10px',
                            borderRadius: '6px',
                            fontSize: '12px',
                            cursor: 'pointer'
                          }}
                        >
                          Удалить
                        </button>
                      </div>
                    ) : (
                      <label style={{ cursor: 'pointer' }}>
                        📎 Выберите изображение
                        <input type="file" accept="image/*" onChange={handleImageUpload} style={{ display: 'none' }} />
                      </label>
                    )}
                  </div>
                </div>

                <div style={{ marginBottom: '15px' }}>
                  <label>Варианты ответов</label>
                  {q.options.map((opt, oIndex) => (
                    <div key={oIndex} style={{ display: 'flex', alignItems: 'center', gap: '10px', marginBottom: '10px' }}>
                      <input
                        type="radio"
                        name="correct"
                        checked={q.correct === oIndex}
                        onChange={() => updateQuestion('correct', oIndex)}
                      />
                      <input
                        type="text"
                        className="input"
                        value={opt}
                        onChange={(e) => updateOption(oIndex, e.target.value)}
                        placeholder={`Вариант ${oIndex + 1}`}
                        style={{ flex: 1, padding: '12px' }}
                      />
                    </div>
                  ))}
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Кнопки навигации */}
        <div style={{ display: 'flex', gap: '10px', marginTop: '20px' }}>
          <button onClick={goToPrev} className="btn" style={{ flex: 1 }}>
            {currentQuestionIndex === 0 ? 'Отмена' : 'Назад'}
          </button>
          <button onClick={addQuestion} className="btn" style={{ flex: 1 }}>
            + Добавить вопрос
          </button>
          <button onClick={goToNext} className="btn-primary" style={{ flex: 1 }}>
            {currentQuestionIndex === questions.length - 1 ? 'Сохранить' : 'Далее'}
          </button>
        </div>
      </div>
    </>
  );
}
