import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/Header';

// Функция для работы с пользователями
const getUsers = () => {
  const users = localStorage.getItem('mockUsers');
  return users ? JSON.parse(users) : [
    { username: 'admin', password: '123' },
    { username: 'user', password: 'password' }
  ];
};

// Добавляем нового пользователя
const addUser = (username, password) => {
  const users = getUsers();
  const exists = users.some(u => u.username === username);
  if (exists) return false;
  users.push({ username, password });
  localStorage.setItem('mockUsers', JSON.stringify(users));
  return true;
};

export default function Register() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirm, setConfirm] = useState('');
  const [showPasswordModal, setShowPasswordModal] = useState(false);
  const [showUsernameModal, setShowUsernameModal] = useState(false);
  const [airplaneVisible, setAirplaneVisible] = useState(false);
  const [airplaneStyle, setAirplaneStyle] = useState({});
  const navigate = useNavigate();

  // Функция для появления самолёта
  const showAirplane = () => {
    const duration = 4000; // 4 секунды полёта
    const startX = Math.random() > 0.5 ? -100 : window.innerWidth + 50; // слева или справа
    const endX = startX < 0 ? window.innerWidth + 50 : -100;
    const top = 20 + Math.random() * (window.innerHeight - 100); // случайная высота

    setAirplaneStyle({
      left: startX,
      top,
      transition: `left ${duration}ms linear`,
      transform: startX < 0 ? 'rotate(0deg)' : 'rotate(180deg)' // смотрит в сторону движения
    });

    setAirplaneVisible(true);

    // Скрываем после анимации
    setTimeout(() => {
      setAirplaneVisible(false);
    }, duration);

    // Запланируем следующий полёт
    setTimeout(showAirplane, 10000 + Math.random() * 20000); // каждые 10–30 сек
  };

  useEffect(() => {
    // Запускаем первый полёт через 3–8 секунд
    const timeout = setTimeout(showAirplane, 3000 + Math.random() * 5000);
    return () => clearTimeout(timeout);
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (password !== confirm) {
      setShowPasswordModal(true);
      return;
    }

    const success = addUser(username, password);
    if (!success) {
      setShowPasswordModal(false);
      setShowUsernameModal(true);
      return;
    }

    localStorage.setItem('user', username);
    navigate('/main');
  };

  const closePasswordModal = () => {
    setShowPasswordModal(false);
  };

  const closeUsernameModal = () => {
    setShowUsernameModal(false);
  };

  return (
    <>
      {/* Самолёт */}
      <div
        style={{
          ...airplaneStyle,
          position: 'fixed',
          width: '60px',
          height: '30px',
          background: `url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="%23d32f2f"><path d="M21 16v-2l-8-5V3.5c0-.83-.67-1.5-1.5-1.5S10 2.67 10 3.5V9l-8 5v2l8-2.5V19l-2 1.5V22l3.5-1 3.5 1v-1.5L13 19v-5.5l8 2.5z"/></svg>') no-repeat center / contain`,
          opacity: airplaneVisible ? 1 : 0,
          pointerEvents: 'none',
          zIndex: 1,
          transition: 'opacity 0.3s ease'
        }}
      />

      {/* Модальное окно: пароли не совпадают */}
      {showPasswordModal && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            zIndex: 1000
          }}
          onClick={closePasswordModal}
        >
          <div
            style={{
              background: 'white',
              borderRadius: '12px',
              padding: '30px',
              width: '90%',
              maxWidth: '400px',
              textAlign: 'center',
              boxShadow: '0 10px 30px rgba(0,0,0,0.2)'
            }}
            onClick={(e) => e.stopPropagation()}
          >
            <h3 style={{ color: '#d32f2f', marginBottom: '15px' }}>Ошибка</h3>
            <p style={{ color: '#555', marginBottom: '20px' }}>
              Пароли не совпадают!
            </p>
            <button onClick={closePasswordModal} className="btn-primary" style={{ width: '100%' }}>
              Понятно
            </button>
          </div>
        </div>
      )}

      {/* Модальное окно: никнейм уже занят */}
      {showUsernameModal && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            zIndex: 1000
          }}
          onClick={closeUsernameModal}
        >
          <div
            style={{
              background: 'white',
              borderRadius: '12px',
              padding: '30px',
              width: '90%',
              maxWidth: '400px',
              textAlign: 'center',
              boxShadow: '0 10px 30px rgba(0,0,0,0.2)'
            }}
            onClick={(e) => e.stopPropagation()}
          >
            <h3 style={{ color: '#d32f2f', marginBottom: '15px' }}>Ошибка</h3>
            <p style={{ color: '#555', marginBottom: '20px' }}>
              Пользователь с таким никнеймом уже существует.
            </p>
            <button onClick={closeUsernameModal} className="btn-primary" style={{ width: '100%' }}>
              Понятно
            </button>
          </div>
        </div>
      )}

      {/* Основная форма */}
      <div className="container" style={{ width: '1000px' }}>
        <Header />
        <div style={{
          display: 'flex',
          background: 'white',
          borderRadius: '20px',
          overflow: 'hidden',
          boxShadow: '0 15px 40px rgba(0,0,0,0.15)',
          maxWidth: '1000px',
          margin: '0 auto'
        }}>
          <div style={{ flex: 1, padding: '40px', background: '#f8f9fa' }}>
            <h1 style={{ fontSize: '28px', color: '#2d3748' }}>
              Присоединяйтесь к платформе IQ-Движок!
            </h1>
            <p>Создайте аккаунт, чтобы получить доступ к увлекательным викторинам.</p>
            <div style={{ marginTop: 'auto', textAlign: 'center', padding: '20px 0' }}>
              <img src="gif.gif" alt="Доп. логотип" style={{ height: '250px', opacity: 0.8 }} />
            </div>
          </div>

          <div style={{ flex: 1.2, padding: '40px' }}>
            <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>
              Создать аккаунт
            </h2>
            <form onSubmit={handleSubmit}>
              <div style={{ marginBottom: '20px' }}>
                <label>Никнейм</label>
                <input
                  type="text"
                  className="input"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  placeholder="Придумайте никнейм"
                  required
                  style={{ marginTop: '8px', width: '100%' }}
                />
              </div>

              <div style={{ marginBottom: '20px' }}>
                <label>Пароль</label>
                <input
                  type="password"
                  className="input"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Придумайте пароль"
                  required
                  style={{ marginTop: '8px', width: '100%' }}
                />
              </div>

              <div style={{ marginBottom: '20px' }}>
                <label>Подтверждение пароля</label>
                <input
                  type="password"
                  className="input"
                  value={confirm}
                  onChange={(e) => setConfirm(e.target.value)}
                  placeholder="Повторите пароль"
                  required
                  style={{ marginTop: '8px', width: '100%' }}
                />
              </div>

              <button type="submit" className="btn-primary">
                Зарегистрироваться
              </button>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}
