import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/Header';

export default function Register() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirm, setConfirm] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirm, setShowConfirm] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    if (password !== confirm) {
      setShowModal(true);
      return;
    }
    localStorage.setItem('user', username);
    navigate('/main');
  };

  const closeModal = () => {
    setShowModal(false);
  };

  return (
    <>
      <Header />

      {/* Модальное окно */}
      {showModal && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            zIndex: 1000
          }}
          onClick={closeModal}
        >
          <div
            style={{
              background: 'white',
              borderRadius: '12px',
              padding: '30px',
              width: '90%',
              maxWidth: '400px',
              textAlign: 'center',
              boxShadow: '0 10px 30px rgba(0,0,0,0.2)'
            }}
            onClick={(e) => e.stopPropagation()}
          >
            <h3 style={{ color: '#d32f2f', marginBottom: '15px' }}>Ошибка</h3>
            <p style={{ color: '#555', marginBottom: '20px' }}>
              Пароли не совпадают!
            </p>
            <button onClick={closeModal} className="btn-primary" style={{ width: '100%' }}>
              Понятно
            </button>
          </div>
        </div>
      )}

      {/* Основная форма */}
      <div className="container" style={{ width: '1000px' }}>
        <div
          style={{
            display: 'flex',
            background: 'white',
            borderRadius: '20px',
            overflow: 'hidden',
            boxShadow: '0 15px 40px rgba(0,0,0,0.15)',
            maxWidth: '1000px',
            margin: '0 auto'
          }}
        >
          <div style={{ flex: 1, padding: '40px', background: '#f8f9fa' }}>
            <h1 style={{ fontSize: '28px', color: '#2d3748' }}>
              Присоединяйтесь к платформе IQ-Движок!
            </h1>
            <p>Создайте аккаунт, чтобы получить доступ к викторинам.</p>
            <div style={{ marginTop: 'auto', textAlign: 'center', padding: '20px 0' }}>
              <img src="gif.gif" alt="Доп. логотип" style={{ height: '250px', opacity: 0.8 }} />
            </div>
          </div>

          <div style={{ flex: 1.2, padding: '40px' }}>
            <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>
              Создать аккаунт
            </h2>
            <form onSubmit={handleSubmit}>
              <div style={{ marginBottom: '20px' }}>
                <label>Никнейм</label>
                <input
                  type="text"
                  className="input"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  placeholder="Придумайте никнейм"
                  required
                  style={{ marginTop: '8px', width: '100%' }}
                />
              </div>

              {/* Поле с паролем и глазиком */}
              <div style={{ marginBottom: '20px' }}>
                <label>Пароль</label>
                <div style={{ position: 'relative', marginTop: '8px' }}>
                  <input
                    type={showPassword ? 'text' : 'password'}
                    className="input"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Придумайте пароль"
                    required
                    style={{ width: '100%', paddingRight: '45px' }}
                  />
                  <button
                    type="button"
                    onClick={() => setShowPassword(prev => !prev)}
                    style={{
                      position: 'absolute',
                      right: '12px',
                      top: '50%',
                      transform: 'translateY(-50%)',
                      background: 'none',
                      border: 'none',
                      color: '#a0aec0',
                      cursor: 'pointer',
                      fontSize: '16px'
                    }}
                  >
                    {showPassword ? (
                      <i className="far fa-eye-slash"></i>
                    ) : (
                      <i className="far fa-eye"></i>
                    )}
                  </button>
                </div>
              </div>

              {/* Поле с подтверждением пароля и глазиком */}
              <div style={{ marginBottom: '20px' }}>
                <label>Подтверждение пароля</label>
                <div style={{ position: 'relative', marginTop: '8px' }}>
                  <input
                    type={showConfirm ? 'text' : 'password'}
                    className="input"
                    value={confirm}
                    onChange={(e) => setConfirm(e.target.value)}
                    placeholder="Повторите пароль"
                    required
                    style={{ width: '100%', paddingRight: '45px' }}
                  />
                  <button
                    type="button"
                    onClick={() => setShowConfirm(prev => !prev)}
                    style={{
                      position: 'absolute',
                      right: '12px',
                      top: '50%',
                      transform: 'translateY(-50%)',
                      background: 'none',
                      border: 'none',
                      color: '#a0aec0',
                      cursor: 'pointer',
                      fontSize: '16px'
                    }}
                  >
                    {showConfirm ? (
                      <i className="far fa-eye-slash"></i>
                    ) : (
                      <i className="far fa-eye"></i>
                    )}
                  </button>
                </div>
              </div>

              <button type="submit" className="btn-primary">
                Зарегистрироваться
              </button>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}
