import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header";

// Функция для работы с пользователями
const getUsers = () => {
  const users = localStorage.getItem("mockUsers");
  return users
    ? JSON.parse(users)
    : [
        { username: "admin", password: "123" },
        { username: "user", password: "password" },
      ];
};

// Добавляем нового пользователя
const addUser = (username, password) => {
  const users = getUsers();
  const exists = users.some((u) => u.username === username);
  if (exists) return false;
  users.push({ username, password });
  localStorage.setItem("mockUsers", JSON.stringify(users));
  return true;
};

export default function Register() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [confirm, setConfirm] = useState("");
  const [showPasswordModal, setShowPasswordModal] = useState(false);
  const [showUsernameModal, setShowUsernameModal] = useState(false);
  const [airplaneVisible, setAirplaneVisible] = useState(false);
  const [darkMode, setDarkMode] = useState(() => {
    const saved = localStorage.getItem("darkMode");
    return saved ? JSON.parse(saved) : false;
  });
  const navigate = useNavigate();

  // Сохраняем тему
  useEffect(() => {
    localStorage.setItem("darkMode", JSON.stringify(darkMode));
    // Добавляем класс к body
    if (darkMode) {
      document.body.classList.add("dark-mode");
    } else {
      document.body.classList.remove("dark-mode");
    }
  }, [darkMode]);

  // Функция для анимации самолёта
  const flyAirplane = () => {
    setAirplaneVisible(true);
    setTimeout(() => setAirplaneVisible(false), 3500);
    setTimeout(flyAirplane, 15000 + Math.random() * 15000);
  };

  useEffect(() => {
    const timer = setTimeout(flyAirplane, 1000 + Math.random() * 2000);
    return () => clearTimeout(timer);
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (password !== confirm) {
      setShowPasswordModal(true);
      return;
    }

    const success = addUser(username, password);
    if (!success) {
      setShowPasswordModal(false);
      setShowUsernameModal(true);
      return;
    }

    localStorage.setItem("user", username);
    navigate("/main");
  };

  const closePasswordModal = () => {
    setShowPasswordModal(false);
  };

  const closeUsernameModal = () => {
    setShowUsernameModal(false);
  };

  // Цвета
  const bg = darkMode ? "#000" : "#f7fafc";
  const cardBg = darkMode ? "#1a1a1a" : "white";
  const text = darkMode ? "#e2e8f0" : "#2d3748";
  const inputBg = darkMode ? "#2d2d2d" : "white";
  const inputBorder = darkMode ? "#444" : "#e2e8f0";

  return (
    <>
      {/* Самолёт */}
      <div
        style={{
          position: "fixed",
          top: "40px",
          left: `${-200 * 1.3}px`,
          width: "200px",
          height: "80px",
          background: `url('/airplane.png') no-repeat center / contain`,
          opacity: airplaneVisible ? 1 : 0,
          pointerEvents: "none",
          zIndex: 1,
          transition: "opacity 0.5s ease-in-out",
          animation: airplaneVisible ? "flyRight 3.5s linear forwards" : "none",
        }}
      />

      <style jsx>{`
        @keyframes flyRight {
          from {
            transform: translateX(0);
          }
          to {
            transform: translateX(calc(100vw + 260px));
          }
        }

        /* Глобальный стиль для body */
        body {
          background: ${darkMode ? "#000" : "#f7fafc"};
          transition: background 0.4s ease;
          margin: 0;
          font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen', 'Ubuntu', 'Cantarell', 'Fira Sans', 'Droid Sans', 'Helvetica Neue', sans-serif;
        }

        /* Стиль тумблера */
        .switch {
          position: relative;
          display: inline-block;
          width: 60px;
          height: 30px;
          margin: 0 10px;
        }

        .switch input {
          opacity: 0;
          width: 0;
          height: 0;
        }

        .slider {
          position: absolute;
          cursor: pointer;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          background-color: #ccc;
          transition: 0.4s;
          border-radius: 34px;
        }

        .slider:before {
          position: absolute;
          content: "";
          height: 22px;
          width: 22px;
          left: 4px;
          bottom: 4px;
          background-color: white;
          transition: 0.4s;
          border-radius: 50%;
        }

        input:checked + .slider {
          background-color: #d32f2f;
        }

        input:checked + .slider:before {
          transform: translateX(30px);
        }
      `}</style>

      {/* Модальные окна — с учётом темы */}
      {showPasswordModal && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            backgroundColor: "rgba(0, 0, 0, 0.7)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: 1000,
          }}
          onClick={closePasswordModal}
        >
          <div
            style={{
              background: darkMode ? "#1a1a1a" : "white",
              borderRadius: "12px",
              padding: "30px",
              width: "90%",
              maxWidth: "400px",
              textAlign: "center",
              boxShadow: "0 10px 30px rgba(0,0,0,0.4)",
              border: darkMode ? "1px solid #444" : "none",
            }}
            onClick={(e) => e.stopPropagation()}
          >
            <h3 style={{ color: "#d32f2f", marginBottom: "15px" }}>Ошибка</h3>
            <p style={{ color: darkMode ? "#ccc" : "#555", marginBottom: "20px" }}>
              Пароли не совпадают!
            </p>
            <button
              onClick={closePasswordModal}
              className="btn-primary"
              style={{ width: "100%" }}
            >
              Понятно
            </button>
          </div>
        </div>
      )}

      {showUsernameModal && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            backgroundColor: "rgba(0, 0, 0, 0.7)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: 1000,
          }}
          onClick={closeUsernameModal}
        >
          <div
            style={{
              background: darkMode ? "#1a1a1a" : "white",
              borderRadius: "12px",
              padding: "30px",
              width: "90%",
              maxWidth: "400px",
              textAlign: "center",
              boxShadow: "0 10px 30px rgba(0,0,0,0.4)",
              border: darkMode ? "1px solid #444" : "none",
            }}
            onClick={(e) => e.stopPropagation()}
          >
            <h3 style={{ color: "#d32f2f", marginBottom: "15px" }}>Ошибка</h3>
            <p style={{ color: darkMode ? "#ccc" : "#555", marginBottom: "20px" }}>
              Пользователь с таким никнеймом уже существует.
            </p>
            <button
              onClick={closeUsernameModal}
              className="btn-primary"
              style={{ width: "100%" }}
            >
              Понятно
            </button>
          </div>
        </div>
      )}

      {/* Основная форма */}
      <div
        className="container"
        style={{
          width: "1000px",
          transition: "background 0.4s ease",
        }}
      >
        {/* Тумблер темы */}
        <div
          style={{
            position: "absolute",
            top: "20px",
            right: "40px",
            display: "flex",
            alignItems: "center",
            zIndex: 10,
            background: darkMode ? "rgba(0,0,0,0.7)" : "rgba(255,255,255,0.8)",
            padding: "8px 16px",
            borderRadius: "20px",
            backdropFilter: "blur(5px)",
            border: darkMode ? "1px solid #444" : "1px solid #e2e8f0",
          }}
        >
          <span style={{ marginRight: "8px", fontSize: "16px", color: text }}>🌙</span>
          <label className="switch">
            <input
              type="checkbox"
              checked={darkMode}
              onChange={() => setDarkMode(prev => !prev)}
            />
            <span className="slider"></span>
          </label>
          <span style={{ marginLeft: "8px", fontSize: "16px", color: text }}>☀️</span>
        </div>

        <Header />
        <div
          style={{
            display: "flex",
            background: cardBg,
            borderRadius: "20px",
            overflow: "hidden",
            boxShadow: darkMode
              ? "0 15px 40px rgba(0,0,0,0.6)"
              : "0 15px 40px rgba(0,0,0,0.15)",
            maxWidth: "1000px",
            margin: "0 auto",
            transition: "all 0.4s ease",
          }}
        >
          <div
            style={{
              flex: 1,
              padding: "40px",
              background: darkMode ? "#121212" : "#f8f9fa",
              transition: "background 0.4s ease",
            }}
          >
            <h1
              style={{
                fontSize: "28px",
                color: text,
                transition: "color 0.3s ease",
              }}
            >
              Присоединяйтесь к платформе IQ-Движок!
            </h1>
            <p
              style={{
                color: darkMode ? "#999" : "#718096",
                transition: "color 0.3s ease",
              }}
            >
              Создайте аккаунт, чтобы получить доступ к увлекательным викторинам.
            </p>
            <div
              style={{
                marginTop: "auto",
                textAlign: "center",
                padding: "20px 0",
              }}
            >
              <img
                src="gif.gif"
                alt="Доп. логотип"
                style={{
                  height: "175px",
                  marginTop: "40px",
                  filter: darkMode ? "brightness(0.9) contrast(1.1)" : "none",
                }}
              />
            </div>
          </div>

          <div style={{ flex: 1.2, padding: "40px" }}>
            <h2
              style={{
                textAlign: "center",
                marginBottom: "20px",
                color: text,
                transition: "color 0.3s ease",
              }}
            >
              Создать аккаунт
            </h2>
            <form onSubmit={handleSubmit}>
              <div style={{ marginBottom: "20px" }}>
                <label
                  style={{
                    color: text,
                    transition: "color 0.3s ease",
                  }}
                >
                  Никнейм
                </label>
                <input
                  type="text"
                  className="input"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  placeholder="Придумайте никнейм"
                  required
                  style={{
                    marginTop: "8px",
                    width: "100%",
                    background: inputBg,
                    color: text,
                    borderColor: inputBorder,
                    padding: "16px",
                    borderRadius: "10px",
                    border: "1px solid",
                    transition: "all 0.3s ease",
                  }}
                />
              </div>

              <div style={{ marginBottom: "20px" }}>
                <label
                  style={{
                    color: text,
                    transition: "color 0.3s ease",
                  }}
                >
                  Пароль
                </label>
                <input
                  type="password"
                  className="input"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Придумайте пароль"
                  required
                  style={{
                    marginTop: "8px",
                    width: "100%",
                    background: inputBg,
                    color: text,
                    borderColor: inputBorder,
                    padding: "16px",
                    borderRadius: "10px",
                    border: "1px solid",
                    transition: "all 0.3s ease",
                  }}
                />
              </div>

              <div style={{ marginBottom: "20px" }}>
                <label
                  style={{
                    color: text,
                    transition: "color 0.3s ease",
                  }}
                >
                  Подтверждение пароля
                </label>
                <input
                  type="password"
                  className="input"
                  value={confirm}
                  onChange={(e) => setConfirm(e.target.value)}
                  placeholder="Повторите пароль"
                  required
                  style={{
                    marginTop: "8px",
                    width: "100%",
                    background: inputBg,
                    color: text,
                    borderColor: inputBorder,
                    padding: "16px",
                    borderRadius: "10px",
                    border: "1px solid",
                    transition: "all 0.3s ease",
                  }}
                />
              </div>

              <button
                type="submit"
                className="btn-primary"
                style={{
                  width: "100%",
                  background: "#d32f2f",
                  padding: "14px",
                  borderRadius: "10px",
                  fontSize: "16px",
                  transition: "background 0.3s ease",
                }}
              >
                Зарегистрироваться
              </button>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}
