// src/pages/Admin.jsx
import { useState } from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header';

export default function Admin() {
  const [title, setTitle] = useState('');
  const [desc, setDesc] = useState('');
  const [questions, setQuestions] = useState([{ text: '', image: null, options: ['', '', '', ''], correct: 0 }]);

  const addQuestion = () => {
    setQuestions([...questions, { text: '', image: null, options: ['', '', '', ''], correct: 0 }]);
  };

  const updateQuestion = (index, field, value) => {
    const newQuestions = [...questions];
    newQuestions[index][field] = value;
    setQuestions(newQuestions);
  };

  const updateOption = (qIndex, oIndex, value) => {
    const newQuestions = [...questions];
    newQuestions[qIndex].options[oIndex] = value;
    setQuestions(newQuestions);
  };

  const handleImageUpload = (qIndex, e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        updateQuestion(qIndex, 'image', reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const removeImage = (qIndex) => {
    updateQuestion(qIndex, 'image', null);
  };

  const saveQuiz = () => {
    const quiz = { title, desc, questions };
    console.log('–°–æ—Ö—Ä–∞–Ω—ë–Ω–Ω–∞—è –≤–∏–∫—Ç–æ—Ä–∏–Ω–∞:', quiz);
    alert('–í–∏–∫—Ç–æ—Ä–∏–Ω–∞ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∞ –≤ –∫–æ–Ω—Å–æ–ª–∏. –ü–æ–∑–∂–µ –æ—Ç–ø—Ä–∞–≤–∏—Ç—Å—è –Ω–∞ –±—ç–∫–µ–Ω–¥.');
  };

  return (
    <>
      <Header />
      <div className="container">
        <h1 style={{ fontSize: '28px', color: '#2d3748', marginBottom: '20px' }}>–ê–¥–º–∏–Ω-–ø–∞–Ω–µ–ª—å</h1>
        <p>–°–æ–∑–¥–∞–π—Ç–µ –Ω–æ–≤—É—é –≤–∏–∫—Ç–æ—Ä–∏–Ω—É</p>

        <div style={{ marginBottom: '20px' }}>
          <label>–ù–∞–∑–≤–∞–Ω–∏–µ –≤–∏–∫—Ç–æ—Ä–∏–Ω—ã</label>
          <input
            type="text"
            className="input"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="–ù–∞–ø—Ä–∏–º–µ—Ä: –ö–æ—Å–º–æ—Å –∏ –∑–≤—ë–∑–¥—ã"
            style={{ marginTop: '8px', width: '100%' }}
          />
        </div>

        <div style={{ marginBottom: '20px' }}>
          <label>–û–ø–∏—Å–∞–Ω–∏–µ</label>
          <input
            type="text"
            className="input"
            value={desc}
            onChange={(e) => setDesc(e.target.value)}
            placeholder="–ö—Ä–∞—Ç–∫–æ–µ –æ–ø–∏—Å–∞–Ω–∏–µ –≤–∏–∫—Ç–æ—Ä–∏–Ω—ã"
            style={{ marginTop: '8px', width: '100%' }}
          />
        </div>

        <button onClick={addQuestion} className="btn" style={{ marginBottom: '20px' }}>
          + –î–æ–±–∞–≤–∏—Ç—å –≤–æ–ø—Ä–æ—Å
        </button>

        {questions.map((q, qIndex) => (
          <div key={qIndex} style={{
            background: '#f8f9fa',
            padding: '25px',
            borderRadius: '15px',
            border: '1px solid #e2e8f0',
            marginBottom: '20px',
            position: 'relative'
          }}>
            <h4 style={{ margin: '0 0 15px 0', color: '#2d3748' }}>–í–æ–ø—Ä–æ—Å {qIndex + 1}</h4>

            <div style={{ marginBottom: '15px' }}>
              <label>–¢–µ–∫—Å—Ç –≤–æ–ø—Ä–æ—Å–∞</label>
              <input
                type="text"
                className="input"
                value={q.text}
                onChange={(e) => updateQuestion(qIndex, 'text', e.target.value)}
                placeholder="–í–≤–µ–¥–∏—Ç–µ –≤–æ–ø—Ä–æ—Å"
                style={{ marginTop: '8px', width: '100%' }}
              />
            </div>

            {/* –ó–∞–≥—Ä—É–∑–∫–∞ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è */}
            <div style={{ marginBottom: '15px' }}>
              <label>–ò–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ –∫ –≤–æ–ø—Ä–æ—Å—É (–æ–ø—Ü–∏–æ–Ω–∞–ª—å–Ω–æ)</label>
              <div style={{
                border: '2px dashed #e2e8f0',
                borderRadius: '10px',
                padding: '15px',
                textAlign: 'center',
                backgroundColor: '#f7fafc',
                cursor: 'pointer',
                marginTop: '8px'
              }}>
                {q.image ? (
                  <div>
                    <img
                      src={q.image}
                      alt="–ü—Ä–µ–≤—å—é"
                      style={{ maxHeight: '150px', maxWidth: '100%', borderRadius: '8px', marginBottom: '10px' }}
                    />
                    <button
                      onClick={() => removeImage(qIndex)}
                      style={{
                        background: '#e53e3e',
                        color: 'white',
                        border: 'none',
                        padding: '5px 10px',
                        borderRadius: '6px',
                        fontSize: '12px',
                        cursor: 'pointer'
                      }}
                    >
                      –£–¥–∞–ª–∏—Ç—å –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ
                    </button>
                  </div>
                ) : (
                  <label style={{ cursor: 'pointer', display: 'block' }}>
                    üìé –í—ã–±–µ—Ä–∏—Ç–µ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ
                    <input
                      type="file"
                      accept="image/*"
                      onChange={(e) => handleImageUpload(qIndex, e)}
                      style={{ display: 'none' }}
                    />
                  </label>
                )}
              </div>
            </div>

            {/* –í–∞—Ä–∏–∞–Ω—Ç—ã –æ—Ç–≤–µ—Ç–æ–≤ */}
            <div style={{ marginBottom: '15px' }}>
              <label>–í–∞—Ä–∏–∞–Ω—Ç—ã –æ—Ç–≤–µ—Ç–æ–≤</label>
              {q.options.map((opt, oIndex) => (
                <div key={oIndex} style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '10px',
                  marginBottom: '10px'
                }}>
                  <input
                    type="radio"
                    name={`correct-${qIndex}`}
                    checked={q.correct === oIndex}
                    onChange={() => updateQuestion(qIndex, 'correct', oIndex)}
                  />
                  <input
                    type="text"
                    className="input"
                    value={opt}
                    onChange={(e) => updateOption(qIndex, oIndex, e.target.value)}
                    placeholder={`–í–∞—Ä–∏–∞–Ω—Ç ${oIndex + 1}`}
                    style={{ flex: 1 }}
                  />
                </div>
              ))}
            </div>

            {/* –ö–Ω–æ–ø–∫–∞ —É–¥–∞–ª–µ–Ω–∏—è –≤–æ–ø—Ä–æ—Å–∞ */}
            {questions.length > 1 && (
              <button
                onClick={() => setQuestions(questions.filter((_, i) => i !== qIndex))}
                style={{
                  position: 'absolute',
                  top: '20px',
                  right: '20px',
                  background: '#e53e3e',
                  color: 'white',
                  border: 'none',
                  borderRadius: '6px',
                  padding: '6px 10px',
                  fontSize: '12px',
                  cursor: 'pointer'
                }}
              >
                –£–¥–∞–ª–∏—Ç—å
              </button>
            )}
          </div>
        ))}

        <button onClick={saveQuiz} className="btn-primary" style={{ width: '100%', marginBottom: '15px' }}>
          –°–æ—Ö—Ä–∞–Ω–∏—Ç—å –≤–∏–∫—Ç–æ—Ä–∏–Ω—É
        </button>

        <Link to="/main" className="btn" style={{ width: '100%', background: 'transparent', color: '#718096', borderColor: '#718096' }}>
          –ù–∞–∑–∞–¥
        </Link>
      </div>
    </>
  );
}
