// src/components/PasswordInput.jsx
import { useState, useRef, useEffect } from 'react';

export default function PasswordInput({ label, value, onChange, name }) {
  const [showPassword, setShowPassword] = useState(false);
  const inputRef = useRef(null);
  const iconRef = useRef(null);

  const togglePassword = () => {
    setShowPassword(prev => !prev);
  };

  useEffect(() => {
    const input = inputRef.current;
    const icon = iconRef.current;

    if (input && icon) {
      const passwordType = showPassword ? 'text' : 'password';
      input.type = passwordType;
      icon.innerHTML = showPassword
        ? '<i class="far fa-eye-slash"></i>'
        : '<i class="far fa-eye"></i>';
    }
  }, [showPassword]);

  return (
    <div style={{ marginBottom: '20px' }}>
      <label>{label}</label>
      <div className="input-with-icon" style={{ position: 'relative', marginTop: '8px' }}>
        <i className="fas fa-lock"></i>
        <input
          ref={inputRef}
          type="password"
          className="input"
          value={value}
          onChange={onChange}
          name={name}
          style={{ paddingLeft: '45px' }}
        />
        <span
          ref={iconRef}
          onClick={togglePassword}
          style={{
            position: 'absolute',
            right: '15px',
            top: '50%',
            transform: 'translateY(-50%)',
            color: '#a0aec0',
            cursor: 'pointer',
            zIndex: 10,
          }}
          dangerouslySetInnerHTML={{ __html: '<i class="far fa-eye"></i>' }}
        />
      </div>
    </div>
  );
}
