import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header";

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è —Ä–∞–±–æ—Ç—ã —Å –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è–º–∏
const getUsers = () => {
  const users = localStorage.getItem("mockUsers");
  return users
    ? JSON.parse(users)
    : [
        { username: "admin", password: "123" },
        { username: "user", password: "password" },
      ];
};

// –î–æ–±–∞–≤–ª—è–µ–º –Ω–æ–≤–æ–≥–æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
const addUser = (username, password) => {
  const users = getUsers();
  const exists = users.some((u) => u.username === username);
  if (exists) return false;
  users.push({ username, password });
  localStorage.setItem("mockUsers", JSON.stringify(users));
  return true;
};

export default function Register() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [confirm, setConfirm] = useState("");
  const [showPasswordModal, setShowPasswordModal] = useState(false);
  const [showUsernameModal, setShowUsernameModal] = useState(false);
  const [airplaneVisible, setAirplaneVisible] = useState(false);
  const navigate = useNavigate();

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –∑–∞–ø—É—Å–∫–∞ –∞–Ω–∏–º–∞—Ü–∏–∏ —Å–∞–º–æ–ª—ë—Ç–∞
  const flyAirplane = () => {
    setAirplaneVisible(true);

    // –ß–µ—Ä–µ–∑ 3.5 —Å–µ–∫—É–Ω–¥—ã ‚Äî —Å–∫—Ä—ã–≤–∞–µ–º (–¥–æ–ª–∂–Ω–æ —Å–æ–≤–ø–∞–¥–∞—Ç—å —Å duration –≤ CSS)
    setTimeout(() => {
      setAirplaneVisible(false);
    }, 3500);

    // –ó–∞–ø–ª–∞–Ω–∏—Ä—É–µ–º —Å–ª–µ–¥—É—é—â–∏–π –ø–æ–ª—ë—Ç —á–µ—Ä–µ–∑ 15‚Äì30 —Å–µ–∫
    setTimeout(flyAirplane, 15000 + Math.random() * 15000);
  };

  useEffect(() => {
    // –ü–µ—Ä–≤—ã–π –ø–æ–ª—ë—Ç —á–µ—Ä–µ–∑ 5‚Äì10 —Å–µ–∫—É–Ω–¥ –ø–æ—Å–ª–µ –∑–∞–≥—Ä—É–∑–∫–∏
    const timer = setTimeout(flyAirplane, 1000 + Math.random() * 2000);
    return () => clearTimeout(timer);
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (password !== confirm) {
      setShowPasswordModal(true);
      return;
    }

    const success = addUser(username, password);
    if (!success) {
      setShowPasswordModal(false);
      setShowUsernameModal(true);
      return;
    }

    localStorage.setItem("user", username);
    navigate("/main");
  };

  const closePasswordModal = () => {
    setShowPasswordModal(false);
  };

  const closeUsernameModal = () => {
    setShowUsernameModal(false);
  };

  return (
    <>
      {/* –°–∞–º–æ–ª—ë—Ç ‚Äî –ª–µ—Ç–∏—Ç —Å–≤–µ—Ä—Ö—É */}
      <div
        style={{
          position: "fixed",
          top: "40px",
          left: `${-200 * 1.3}px`,
          width: "200px",
          height: "80px",
          background: `url('/airplane.png') no-repeat center / contain`,
          opacity: airplaneVisible ? 1 : 0,
          pointerEvents: "none",
          zIndex: 1,
          transition: "opacity 0.5s ease-in-out",
          animation: airplaneVisible
            ? "flyRight 3.5s linear forwards"
            : "none",
        }}
      />

      <style jsx>{`
        @keyframes flyRight {
          from {
            transform: translateX(0);
          }
          to {
            transform: translateX(calc(100vw + 260px));
          }
        }
      `}</style>

      {/* –ú–æ–¥–∞–ª—å–Ω–æ–µ –æ–∫–Ω–æ: –ø–∞—Ä–æ–ª–∏ –Ω–µ —Å–æ–≤–ø–∞–¥–∞—é—Ç */}
      {showPasswordModal && (
        <div
          style={{
            position: "fixed",
    top: 0,
    left: 0,
    width: "100%",
    height: "100%",
    backgroundColor: "rgba(255, 0, 0, 0.5)",
    border: '200px solid black',
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    zIndex: 1000,
    backgroundClip: 'padding-box' // üî• –ö–ª—é—á–µ–≤–æ–µ —Å–≤–æ–π—Å—Ç–≤–æ
          }}
          onClick={closePasswordModal}
        >
          <div
            style={{
              background: "white",
              borderRadius: "12px",
              padding: "30px",
              width: "90%",
              maxWidth: "400px",
              textAlign: "center",
              boxShadow: "0 10px 30px rgba(0,0,0,0.2)",
            }}
            onClick={(e) => e.stopPropagation()}
          >
            <h3 style={{ color: "rgba(211, 47, 47, 1)", marginBottom: "15px" }}>–û—à–∏–±–∫–∞</h3>
            <p style={{ color: "#555", marginBottom: "20px" }}>
              –ü–∞—Ä–æ–ª–∏ –Ω–µ —Å–æ–≤–ø–∞–¥–∞—é—Ç!
            </p>
            <button
              onClick={closePasswordModal}
              className="btn-primary"
              style={{ width: "100%" }}
            >
              –ü–æ–Ω—è—Ç–Ω–æ
            </button>
          </div>
        </div>
      )}

