import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header';

// Моковые данные (позже замените на fetch)
const mockQuizzes = [
  { id: 1, title: "Космос: Тест на знание Галактики", questions: 10 },
  { id: 2, title: "История России", questions: 15 },
  { id: 3, title: "Технологии 2025", questions: 8 },
];

export default function Main() {
  const [quizzes, setQuizzes] = useState([]);

  useEffect(() => {
    // Здесь будет fetch('/api/quizzes')
    setQuizzes(mockQuizzes);
  }, []);

  const username = localStorage.getItem('user') || 'Пользователь';

  return (
    <>
      <Header />
      <div className="container">
        <h1 style={{ fontSize: '28px', color: '#2d3748', marginBottom: '20px' }}>
          Привет, <strong>{username}</strong>!
        </h1>
        <div style={{ marginBottom: '30px' }}>
          <p>Готовы проверить свои знания?</p>
        </div>

        <h2 style={{ color: '#2d3748', marginBottom: '20px' }}>Популярные викторины</h2>
        <div style={{ display: 'flex', flexDirection: 'column', gap: '20px' }}>
          {quizzes.map(quiz => (
            <div key={quiz.id} style={{
              border: '1px solid #e2e8f0',
              borderRadius: '15px',
              padding: '20px',
              background: '#f8f9fa'
            }}>
              <h3>{quiz.title}</h3>
              <p>{quiz.questions} вопросов</p>
              <Link to={`/quiz/${quiz.id}`} className="btn">Начать</Link>
            </div>
          ))}
        </div>
      </div>
    </>
  );
}
