// src/pages/Admin.jsx
import { useState } from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header';

export default function Admin() {
  const [title, setTitle] = useState('');
  const [desc, setDesc] = useState('');
  const [questions, setQuestions] = useState([
    { text: '', image: null, options: ['', '', ''], correct: 0, optionCount: 2 }
  ]);

  const addQuestion = () => {
    setQuestions([
      ...questions,
      { text: '', image: null, options: ['', '', ''], correct: 0, optionCount: 2 }
    ]);
  };

  const updateQuestion = (index, field, value) => {
    const newQuestions = [...questions];
    newQuestions[index][field] = value;

    // Если изменили количество опций — обновим массив
    if (field === 'optionCount') {
      const count = parseInt(value, 10);
      newQuestions[index].options = Array(count).fill('');
      if (newQuestions[index].correct >= count) {
        newQuestions[index].correct = 0;
      }
    }

    setQuestions(newQuestions);
  };

  const updateOption = (qIndex, oIndex, value) => {
    const newQuestions = [...questions];
    newQuestions[qIndex].options[oIndex] = value;
    setQuestions(newQuestions);
  };

  const handleImageUpload = (qIndex, e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        updateQuestion(qIndex, 'image', reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const removeImage = (qIndex) => {
    updateQuestion(qIndex, 'image', null);
  };

  const removeQuestion = (index) => {
    if (questions.length > 1) {
      setQuestions(questions.filter((_, i) => i !== index));
    }
  };

  const saveQuiz = () => {
    const quiz = { title, desc, questions };
    console.log('Сохранённая викторина:', quiz);
    alert('Викторина сохранена в консоли. Позже будет отправлена на бэкенд.');
  };

  return (
    <>
      <Header />
      <div className="container">
        <h1 style={{ fontSize: '28px', color: '#2d3748', marginBottom: '20px' }}>Админ-панель</h1>
        <p>Создайте новую викторину</p>

        {/* Название викторины */}
        <div style={{ marginBottom: '20px' }}>
          <label>Название викторины</label>
          <input
            type="text"
            className="input"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Например: Космос и звёзды"
            style={{ marginTop: '8px', width: '100%', fontSize: '16px', padding: '15px' }}
          />
        </div>

        {/* Описание */}
        <div style={{ marginBottom: '20px' }}>
          <label>Описание</label>
          <input
            type="text"
            className="input"
            value={desc}
            onChange={(e) => setDesc(e.target.value)}
            placeholder="Краткое описание викторины"
            style={{ marginTop: '8px', width: '100%', fontSize: '16px', padding: '15px' }}
          />
        </div>

        <button onClick={addQuestion} className="btn" style={{ marginBottom: '20px' }}>
          + Добавить вопрос
        </button>

        {questions.map((q, qIndex) => (
          <div
            key={qIndex}
            style={{
              background: '#f8f9fa',
              padding: '25px',
              borderRadius: '15px',
              border: '1px solid #e2e8f0',
              marginBottom: '20px',
              position: 'relative'
            }}
          >
            <h4 style={{ margin: '0 0 15px 0', color: '#2d3748' }}>Вопрос {qIndex + 1}</h4>

            {/* Текст вопроса */}
            <div style={{ marginBottom: '15px' }}>
              <label>Текст вопроса</label>
              <input
                type="text"
                className="input"
                value={q.text}
                onChange={(e) => updateQuestion(qIndex, 'text', e.target.value)}
                placeholder="Введите вопрос"
                style={{ marginTop: '8px', width: '100%', fontSize: '16px', padding: '15px' }}
              />
            </div>

            {/* Количество вариантов ответов */}
            <div style={{ marginBottom: '15px' }}>
              <label>Количество вариантов ответов</label>
              <select
                value={q.optionCount}
                onChange={(e) => updateQuestion(qIndex, 'optionCount', e.target.value)}
                style={{
                  width: '100%',
                  padding: '15px',
                  border: '1px solid #e2e8f0',
                  borderRadius: '10px',
                  fontSize: '16px',
                  marginTop: '8px'
                }}
              >
                <option value={2}>2 варианта</option>
                <option value={3}>3 варианта</option>
                <option value={4}>4 варианта</option>
              </select>
            </div>

            {/* Загрузка изображения */}
            <div style={{ marginBottom: '15px' }}>
              <label>Изображение к вопросу (опционально)</label>
              <div
                style={{
                  border: '2px dashed #e2e8f0',
                  borderRadius: '10px',
                  padding: '15px',
                  textAlign: 'center',
                  backgroundColor: '#f7fafc',
                  cursor: 'pointer',
                  marginTop: '8px'
                }}
              >
                {q.image ? (
                  <div>
                    <img
                      src={q.image}
                      alt="Превью"
                      style={{
                        maxHeight: '150px',
                        maxWidth: '100%',
                        borderRadius: '8px',
                        marginBottom: '10px'
                      }}
                    />
                    <button
                      onClick={() => removeImage(qIndex)}
                      style={{
                        background: '#e53e3e',
                        color: 'white',
                        border: 'none',
                        padding: '5px 10px',
                        borderRadius: '6px',
                        fontSize: '12px',
                        cursor: 'pointer'
                      }}
                    >
                      Удалить изображение
                    </button>
                  </div>
                ) : (
                  <label style={{ cursor: 'pointer', display: 'block' }}>
                    📎 Выберите изображение
                    <input
                      type="file"
                      accept="image/*"
                      onChange={(e) => handleImageUpload(qIndex, e)}
                      style={{ display: 'none' }}
                    />
                  </label>
                )}
              </div>
            </div>

            {/* Варианты ответов */}
            <div style={{ marginBottom: '15px' }}>
              <label>Варианты ответов</label>
              {q.options.map((opt, oIndex) => (
                <div
                  key={oIndex}
                  style={{
                    display: 'flex',
                    alignItems: 'center',
                    gap: '10px',
                    marginBottom: '10px'
                  }}
                >
                  <input
                    type="radio"
                    name={`correct-${qIndex}`}
                    checked={q.correct === oIndex}
                    onChange={() => updateQuestion(qIndex, 'correct', oIndex)}
                  />
                  <input
                    type="text"
                    className="input"
                    value={opt}
                    onChange={(e) => updateOption(qIndex, oIndex, e.target.value)}
                    placeholder={`Вариант ${oIndex + 1}`}
                    style={{ flex: 1, fontSize: '16px', padding: '12px' }}
                  />
                </div>
              ))}
            </div>

            {/* Кнопка удаления вопроса */}
            {questions.length > 1 && (
              <button
                onClick={() => removeQuestion(qIndex)}
                style={{
                  position: 'absolute',
                  top: '20px',
                  right: '20px',
                  background: '#e53e3e',
                  color: 'white',
                  border: 'none',
                  borderRadius: '6px',
                  padding: '6px 10px',
                  fontSize: '12px',
                  cursor: 'pointer'
                }}
              >
                Удалить
              </button>
            )}
          </div>
        ))}

        <button onClick={saveQuiz} className="btn-primary" style={{ width: '100%', marginBottom: '15px' }}>
          Сохранить викторину
        </button>

        <Link
          to="/main"
          className="btn"
          style={{
            width: '100%',
            background: 'transparent',
            color: '#718096',
            borderColor: '#718096'
          }}
        >
          Назад
        </Link>
      </div>
    </>
  );
}
