import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/Header';

export default function Register() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirm, setConfirm] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    if (password !== confirm) {
      alert('Пароли не совпадают!');
      return;
    }
    localStorage.setItem('user', username);
    alert('Регистрация успешна!');
    navigate('/main');
  };

  return (
    <>
      <Header />
      <div className="container" style={{width: '1000px'}} >
        <div style={{ display: 'flex', background: 'white', borderRadius: '20px', overflow: 'hidden', boxShadow: '0 15px 40px rgba(0,0,0,0.15)', maxWidth: '1000px', margin: '0 auto' }}>
          <div style={{ flex: 1, padding: '40px', background: '#f8f9fa' }}>
            <h1 style={{ fontSize: '28px', color: '#2d3748' }}>Присоединяйтесь к платформе IQ-Движок!</h1>
            <p>Создайте аккаунт, чтобы получить доступ к викторинам.</p>
          </div>
          <div style={{ flex: 1.2, padding: '40px' }}>
            <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Создать аккаунт</h2>
            <form onSubmit={handleSubmit}>
              <div style={{ marginBottom: '20px' }}>
                <label>Никнейм</label>
                <input
                  type="text"
                  className="input"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  placeholder="Придумайте никнейм"
                  required
                  style={{ marginTop: '8px', width: '100%' }}
                />
              </div>
              <div style={{ marginBottom: '20px' }}>
                <label>Пароль</label>
                <input
                  type="password"
                  className="input"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Придумайте пароль"
                  required
                  style={{ marginTop: '8px', width: '100%' }}
                />
              </div>
              <div style={{ marginBottom: '20px' }}>
                <label>Подтверждение</label>
                <input
                  type="password"
                  className="input"
                  value={confirm}
                  onChange={(e) => setConfirm(e.target.value)}
                  placeholder="Повторите пароль"
                  required
                  style={{ marginTop: '8px', width: '100%' }}
                />
              </div>

              <button type="submit" className="btn-primary">Зарегистрироваться</button>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}
