import { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Header from '../components/Header';

export default function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    // Здесь будет проверка на бэкенде
    localStorage.setItem('user', username);
    alert('Добро пожаловать!');
    navigate('/main');
  };

  return (
    <>
      <Header />
      <div className="container">
        <div style={{ display: 'flex', background: 'white', borderRadius: '20px', overflow: 'hidden', boxShadow: '0 15px 40px rgba(0,0,0,0.15)', maxWidth: '1000px', margin: '0 auto' }}>
          <div style={{ flex: 1, padding: '40px', background: '#f8f9fa' }}>
            <h1 style={{ fontSize: '28px', color: '#2d3748' }}>Добро пожаловать!</h1>
            <p>Войдите, чтобы продолжить прохождение викторин.</p>
          </div>
          <div style={{ flex: 1.2, padding: '40px' }}>
            <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Войти</h2>
            <form onSubmit={handleSubmit}>
              <div style={{ marginBottom: '20px' }}>
                <label>Никнейм</label>
                <input
                  type="text"
                  className="input"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  placeholder="Введите никнейм"
                  required
                  style={{ marginTop: '8px', width: '100%' }}
                />
              </div>
              <div style={{ marginBottom: '20px' }}>
                <label>Пароль</label>
                <input
                  type="password"
                  className="input"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Введите пароль"
                  required
                  style={{ marginTop: '8px', width: '100%' }}
                />
              </div>
              <button type="submit" className="btn" style={{ width: '100%' }}>Войти</button>
            </form>
            <div style={{ textAlign: 'center', marginTop: '15px' }}>
              <Link to="/">Нет аккаунта? Зарегистрироваться</Link>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
